import React, { useContext, useEffect, useState } from "react";
import Navbar from "./navbar";
import Footer from "./footer";
import { dataContext } from "./DataContext";
import "../css-components/products.css";

const Products = () => {
  const { data, cart, setCart } = useContext(dataContext);
  const [loading, setLoading] = useState(true);

  const buyProducts = (product) => {
    setCart([...cart, product]);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://127.0.0.1:5000/productos");
        if (!response.ok) {
          throw new Error(`Error de red: ${response.status}`);
        }

        const data = await response.json();
        // Actualiza el contexto o el estado local con los datos obtenidos.
        // Por ejemplo, si los datos están en res.data:
        // setData(data);
        setLoading(false); // Indica que la carga ha finalizado
      } catch (error) {
        console.error("Error al recuperar los productos: ", error.message);
        setLoading(false); // Indica que la carga ha finalizado incluso en caso de error
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <Navbar />
      {loading ? (
        // Muestra un mensaje de carga mientras se obtienen los datos
        <div>Cargando...</div>
      ) : (
        <div className="productos">
          {data.map((product) => (
            <div className="producto" key={product._id} onClick={() => buyProducts(product)}>
              <img src={product.url} alt={product.nombre} className="imagen-producto" />
              <h3>{product.nombre}</h3>
              <h3>{product.genero}</h3>
              <h4>${product.costo} CLP</h4>
              <p>Comprar</p>
            </div>
          ))}
          <div className="bottom">
            <button>Ver más</button>
            <div>
              <a href="#">Ver detalles</a>
            </div>
          </div>
        </div>
      )}
      <Footer />
    </div>
  );
};

export default Products;
