import React, {useContext, useEffect } from 'react';
import Navbar from './navbar';
import {Link} from 'react-router-dom';
import { dataContext } from "./DataContext";
import axios from 'axios';
import '../css-components/productAdmin.css';

const ProductsAdmin = () => {
const { data } = useContext(dataContext);

  useEffect(() => {
    axios.get("http://127.0.0.1:5000/productosAdmin")
      .then((res) => {
        
      })
      .catch((error) => {
        console.error("Error al recuperar los productos: ", error);
      });
      axios.get("http://127.0.0.1:5000/usuariorol", { withCredentials: true })
      .then((res) => {
        const rol = res.data.message;
        if (rol === "si") {
          setIsAdmin(true);
          setIsUser(false);
        } else if (rol === "siu") {
          setIsAdmin(false);
          setIsUser(true);
        } else {
          setIsAdmin(false);
          setIsUser(false);
        }
        if(rol == "no" && window.location.href =="http://localhost:3000/productsAdmin" ){
          window.location = "home"
        }
        })
        .catch((error) => {
          console.error("Error al recuperar los productos: ", error);
        });
  }, []);
  return (
      <div>
        <Navbar/>
        <table id='table-admin' className='table table-light'>
          <thead className='thead-dark'>
            <tr>
              <th scope='col'>Nombre</th>
              <th scope='col'>Marca</th>
              <th scope='col'>Genero</th>
              <th scope='col'>Costo</th>
              <th>
              <Link to="/productsNew"
                    className="btn btn-outline-primary"
                    role="button"
                    aria-pressed="true"
                    >
                New Product
              </Link>
              </th>
            </tr>
            </thead>
           <tbody>
                {data.map((product) => {
                  return (
                      <tr key={product._id}>
                        <td >{product.nombre}</td>
                        <td>{product.marca}</td>
                        <td>{product.talla}</td>
                        <td>{product.costo}</td>
                      </tr>
                  );
                })}
            </tbody>  
        </table>
      </div>
    )
}

export default ProductsAdmin;
