import React, { useState, useEffect } from 'react';
import '../css-components/navbar.css';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [showInventoryLink, setShowInventoryLink] = useState(false);

  useEffect(() => {
    const ad = async () => {
      try {
        const res = await fetch("http://127.0.0.1:5000/usuariorol", {
          method: "GET",
          headers: {
            "Content-Type": "application/json"
          },
          mode: 'no-cors'
        });

        if (res.ok) {
          const data = await res.json();
          console.log(data.message);
          
          if (data.message === 'si') {
            setShowInventoryLink(true);
          }
        }
      } catch (error) {
        console.error("Error: ", error);
        // Manejar errores aquÃ­ (puedes mostrar un mensaje de error al usuario, por ejemplo)
      }
    };

    // Llamar a la funciÃ³n 'ad' cuando el componente se monte
    ad();
  }, []); // El segundo argumento del useEffect, [], asegura que el efecto solo se ejecute una vez (cuando el componente se monta)

  return (
    <nav id='navus'>
      <div className='container-fluid'>
        <Link to='/' className='navbar-brand'>Banana Shop</Link>
      </div>
      <div>
        <ul id='navi'>
          <li>
            <Link to='/login'>Login </Link>
          </li>

          <li>
            <Link to='/register'>Register</Link>
          </li>

          <li>
            <Link to='/products'>Products</Link>
          </li>

          {showInventoryLink && (
            <li>
              <Link to="/inventory">Pagina de Inventario</Link>
            </li>
          )}

          <li>
            <Link to="/a">ðŸ›’</Link>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
