import React, { useState } from 'react';
import Navbar from './navbar';
import '../css-components/productsNew.css';
import axios from 'axios';

const ProductsNew = () => {
  const [loading, setLoading] = useState(false);

  const pro = async () => {
    // Tu lógica para enviar datos al servidor
    setLoading(true); // Inicia el estado de carga antes de realizar la solicitud

    try {
      // Tu lógica para enviar la solicitud al servidor
      // ...

      // Después de que la solicitud se complete con éxito o falle, detén el estado de carga
      setLoading(false);
      const res = await axios.get("http://127.0.0.1:5000/usuariorol", { withCredentials: true });
      const rol = res.data.message;
      if (rol === "no") {
        window.location = "home";
      } else {
        // Redirige a la página de administrador o haz cualquier otra acción necesaria
        window.location = "productsAdmin";
      }
    } catch (error) {
      console.error("Error al enviar la solicitud:", error);
      setLoading(false); // Asegúrate de detener el estado de carga en caso de error
    }
  };

  const mandardatos = (event) => {
    event.preventDefault(); // Evitar que el formulario se envíe normalmente
    pro(); // Llamar a la función pro cuando se presiona el botón
  };

  if (loading) {
    return <div>Cargando...</div>;
  }

  return (
    <div>
      <Navbar/>
      {/* Resto de tu código del componente ProductsNew */}
    </div>
  );
};

export default ProductsNew;
