    try {
      const res = await fetch("http://127.0.0.1:5000/registro", {
        method: "GET",
        headers: {
          "Content-Type": "application/json"
        }
      });
  
      // Verificar si la solicitud fue exitosa (cÃ³digo de estado 200)
      if (res.ok) {
        // Convertir la respuesta a formato JSON
        const data = await res.json();

        console.log(data.message); 
        if(data.message == 'si'){
          return 
        }
      }
    } catch (error) {
      console.error("Error: ", error);
      // Manejar errores aquÃ­ (puedes mostrar un mensaje de error al usuario, por ejemplo)
    }
  };

  return (
    <nav id='navus'>
        <div className='container-fluid'>
            <Link to='/' className='navbar-brand'>Banana Shop</Link>
            
        </div>
        <div>
            <ul id='navi'>
                <li>
                  <Link to='/login'>Login </Link>
                </li>

                <li>
                  <Link to='/register'>Register</Link>
                </li>

                <li>
                  <Link to='/products'>Products</Link>
                </li>
                
                <li>
                  <Link to="/a">ðŸ›’</Link>
                </li>


            </ul>
        </div>
    </nav>
  )
}
export default Navbar;
