import './App.css';
import Login from "./components/login.jsx";
import Register from './components/register';
import Products from './components/products';
import Home from './components/home';
import ProductsAdmin from './components/productsAdmin';
import ProductsNew from './components/productsNew'; 
import Carrito  from './components/Carrito/carrito';
import DataProvider from './components/DataContext';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Productos from './components/productos';

function App() {
  useEffect(() => {
    axios.get("http://127.0.0.1:5000/usuariorol", { withCredentials: true })
    .then((res) => {
      const rol = res.data.message;
      if (rol === "si") {
        setIsAdmin(true);
        setIsUser(false);
      } else if (rol === "siu") {
        setIsAdmin(false);
        setIsUser(true);
      } else {
        setIsAdmin(false);
        setIsUser(false);
      }
      if(rol == "no" && window.location.href =="http://localhost:3000/productsNew" ){
        window.location = "home"
        
      }
    })
    .catch((error) => {
      console.error("Error al recuperar los productos: ", error);
    });
  }, []);
  return (
    <div className="App">
        <DataProvider>
          <BrowserRouter>
            <Routes>
                <Route path='/' element={<Home/>}></Route>
                <Route path='/home' element={<Home/>}></Route>
                <Route path='/login' element={<Login/>}></Route>
                <Route path='/register' element={<Register/>}></Route>
                <Route path='/products' element={<Products/>}></Route>
                <Route path='/productsAdmin' element={<ProductsAdmin/>}></Route>
                <Route path='/productsNew' element={<ProductsNew/>}></Route>
                <Route path='/carrito' element={<Carrito/>}></Route>
                <Route path='/productos' element={<Productos/>}></Route>
            </Routes>
          </BrowserRouter>
        </DataProvider>
    </div>
  );
}

export default App;
