import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from './navbar';
import axios from 'axios';

const ProductsEdit = () => {
  const { _id } = useParams();
  const [loading, setLoading] = useState(true);
  const [editedProduct, setEditedProduct] = useState({
    nombre: '',
    categoria: '',
    genero: '',
    talla: '',
    marca: '',
    costo: '',
    color: '',
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`http://127.0.0.1:5000/productos/${_id}`);
        setEditedProduct(res.data);
        setLoading(false);
      } catch (error) {
        console.error('Error al recuperar el producto: ', error);
        setLoading(false);
      }
    };

    fetchData();
  }, [_id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedProduct((prevProduct) => ({ ...prevProduct, [name]: value }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.patch(`http://127.0.0.1:5000/productos/${_id}`, editedProduct);

      if (res.status === 200) {
        alert('Producto actualizado');
      } else {
        console.error('No se pudo actualizar el producto');
      }
    } catch (error) {
      console.error('Error al enviar la solicitud:', error);
    }
  };

  if (loading) {
    return (
      <div>
        <Navbar />
        <div className="loading-container">
          <div className="loading-message">Cargando...</div>
        </div>
      </div>
    );
  }

  return (
    <div>
      <Navbar />
      <h1>Editar Producto</h1>
      <form id="formulario" onSubmit={handleFormSubmit}>
        <div className="form-group">
          <label>Nombre Producto</label>
          <input
            type="text"
            className="form-control"
            placeholder="Nombre"
            name="nombre"
            value={editedProduct.nombre}
            onChange={handleInputChange}
            readOnly
          />
        </div>
        <div className="form-group">
          <label>Categoría</label>
          <input
            type="text"
            className="form-control"
            placeholder="Categoría"
            name="categoria"
            value={editedProduct.categoria}
            onChange={handleInputChange}
          />
        </div>
        <div className="form-group">
          <label>Color</label>
          <input
            type="text"
            className="form-control"
            placeholder="Color"
            name="color"
            value={editedProduct.color}
            onChange={handleInputChange}
          />
        </div>
        <div className="form-group">
          <label>Precio</label>
          <input
            type="text"
            className="form-control"
            placeholder="Precio"
            name="costo"
            value={editedProduct.costo}
            onChange={handleInputChange}
          />
        </div>
        <div className="form-group">
          <label>Talla</label>
          <input
            type="text"
            className="form-control"
            placeholder="Talla"
            name="talla"
            value={editedProduct.talla}
            onChange={handleInputChange}
          />
        </div>
        <div className="form-group">
          <label>Género</label>
          <input
            type="text"
            className="form-control"
            placeholder="Género"
            name="genero"
            value={editedProduct.genero}
            onChange={handleInputChange}
          />
        </div>
        <div className="form-group">
          <label>Marca</label>
          <input
            type="text"
            className="form-control"
            placeholder="Marca"
            name="marca"
            value={editedProduct.marca}
            onChange={handleInputChange}
          />
        </div>
        <button type="submit" className="btn btn-dark">
          Guardar Cambios
        </button>
      </form>
    </div>
  );
};

export default ProductsEdit;
